const hamburgerMenu=document.querySelector("#mobile-menu"),mobileNavigation=document.querySelector("#mobile-nav");hamburgerMenu.addEventListener("click",function(e){e.preventDefault(),hamburgerMenu.classList.toggle("close"),mobileNavigation.classList.toggle("open")});const mobileHref=document.querySelectorAll(".mobile-href");for(let oneHref of mobileHref)oneHref.addEventListener("click",function(){mobileNavigation.classList.remove("open"),hamburgerMenu.classList.remove("close")});